import axios from 'axios';
import React, { useEffect, useState, useCallback, useRef } from 'react';

export default function InputField() {
    const [data, setData] = useState([]);
    const [search, setSearch] = useState('');
    const [filteredOptions, setFilteredOptions] = useState([]);
    const [page, setPage] = useState(1);
    const [isFetching, setIsFetching] = useState(false);
    const [hasMore, setHasMore] = useState(true);

    const containerRef = useRef(null);

    useEffect(() => {
        axios.get('https://softwium.com/api/books')
            .then(res => {
                setData(res.data.slice(0, 10)); // Load initial 10 items
                setFilteredOptions(res.data.slice(0, 10));
            })
            .catch(error => {
                console.error("Error fetching data", error);
            });
    }, []);

    const loadMoreData = useCallback(() => {
        if (isFetching || !hasMore) return;

        setIsFetching(true);

        setTimeout(() => {
            const nextPage = page + 1;
            const newItems = data.slice((nextPage - 1) * 10, nextPage * 10); // Load next 10 items

            if (newItems.length > 0) {
                setFilteredOptions(prevOptions => [...prevOptions, ...newItems]);
                setPage(nextPage);
            } else {
                setHasMore(false);
            }

            setIsFetching(false);
        }, 1000);
    }, [isFetching, hasMore, page, data]);

    const handleSearchChange = (e) => {
        const query = e.target.value.toLowerCase();
        setSearch(query);

        if (query.length > 0) {
            const filtered = data.filter(book =>
                book.title.toLowerCase().includes(query) ||
                book.id.toString() === query);
            setFilteredOptions(filtered);
        } else {
            setFilteredOptions(data.slice(0, page * 10)); 
        }
    };

    const handleScroll = () => {
        if (containerRef.current) {
            const { scrollTop, scrollHeight, clientHeight } = containerRef.current;
            if (scrollTop + clientHeight >= scrollHeight - 5) {
                loadMoreData();
            }
        }
    };

    return (
        <div 
            style={{ height: '500px', overflowY: 'auto', border: '1px solid #ccc' }}
            onScroll={handleScroll}
            ref={containerRef}
        >
            <input
                placeholder='Search for a book'
                onChange={handleSearchChange}
                value={search}
            />
            {filteredOptions.length > 0 ? (
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {filteredOptions.map((book, index) => (
                        <li
                            key={index}
                            style={{ padding: '5px', cursor: 'pointer', border: '1px solid #ccc', margin: '5px 0' }}
                            onClick={() => console.log(`Selected book: ${book.title}, ID: ${book.id}`)}
                        >
                            {book.title} - {book.authors.join(', ')}
                        </li>
                    ))}
                </ul>
            ) : data.map((book, index) =>
                <li key={index}>
                    {book.title} - {book.authors.join(', ')}
                </li>
            )}
            {isFetching && <p>Loading more items...</p>}
        </div>
    )
}
