import { CircularProgress, Container, Typography } from '@mui/material';
import axios from 'axios';
import React, { useEffect, useState, useCallback, useRef } from 'react';
import Loading from './Loading'

export default function InputField() {
    const [data, setData] = useState([]); // All data
    const [search, setSearch] = useState(''); // Search query
    const [filteredOptions, setFilteredOptions] = useState([]); // Filtered data based on search
    const [displayedOptions, setDisplayedOptions] = useState([]); // Data currently displayed in the UI
    const [page, setPage] = useState(1); // Current page for pagination
    const [isFetching, setIsFetching] = useState(false); // Flag to prevent multiple simultaneous fetches
    const [hasMore, setHasMore] = useState(true); // Flag to check if more data is available to load
    const [open ,setOpen] = useState(true)
    const containerRef = useRef(null);

    useEffect(() => {
        axios.get('https://softwium.com/api/books')
            .then(res => {
                setData(res.data);
                setDisplayedOptions(res.data.slice(0, 10)); 
                setOpen(false)
            })
            .catch(error => {
                console.error("Error fetching data", error);
            });
    }, []);

    const loadMoreData = useCallback(() => {
        if (isFetching || !hasMore) return;

        setIsFetching(true);

        setTimeout(() => {
            const nextPage = page + 1;
            const moreItems = (search ? filteredOptions : data).slice((nextPage - 1) * 10, nextPage * 10); // Load next 10 items

            if (moreItems.length > 0) {
                setDisplayedOptions(prevOptions => [...prevOptions, ...moreItems]);
                setPage(nextPage);
            } else {
                setHasMore(false);
            }

            setIsFetching(false);
        }, 1000);
    }, [isFetching, hasMore, page, data, filteredOptions, search]);

    const handleSearchChange = (e) => {
        const query = e.target.value.toLowerCase();
        setSearch(query);
        setPage(1); // Reset to the first page when the search query changes

        if (query.length > 0) {
            const filtered = data.filter(book =>
                book.title.toLowerCase().includes(query) ||
                book.id.toString() === query
            );
            setFilteredOptions(filtered);
            setDisplayedOptions(filtered.slice(0, 10)); // Display the first 10 filtered items
            setHasMore(filtered.length > 10); // Check if there are more items to load
        } else {
            setFilteredOptions([]);
            setDisplayedOptions(data.slice(0, 10)); // Reset to the first 10 items of the entire data
            setHasMore(data.length > 10);
        }
    };

    const handleScroll = () => {
        if (containerRef.current) {
            const { scrollTop, scrollHeight, clientHeight } = containerRef.current;
            if (scrollTop + clientHeight >= scrollHeight - 5) {
                loadMoreData();
            }
        }
    };

    return (
        <div>
            {open && <Loading open={open} />}
            <Container maxWidth='sm' sx={{display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center',gap:'20px'}}>
                <Typography sx={{color:'#7b1fa2',fontWeight:'bolder',fontSize:'20px'}}>Search Component</Typography>
            <input
                placeholder='Search for a book'
                onChange={handleSearchChange}
                value={search}
                className='input'
            />
            {displayedOptions.length > 0 && (
                    <ul 
                        onScroll={handleScroll}
                        ref={containerRef}
                        style={{ listStyleType: 'none', padding: 0 }}
                    >
                        {displayedOptions.map((book, index) => (
                            <li
                                key={index}
                                style={{ padding: '5px', cursor: 'pointer', border: '1px solid #ccc', margin: '5px 0' }}
                                onClick={() => console.log(`Selected book: ${book.title}, ID: ${book.id}`)}
                            >
                                {book.title} - {book.authors.join(', ')}
                            </li>
                        ))}
                        {isFetching && (
                            <li style={{ display: 'flex', justifyContent: 'center', padding: '10px', border:'none' }}>
                                <CircularProgress color="secondary" />
                            </li>
                        )}
                    </ul>
                )}
            </Container>
        </div>
    )
}
