import axios from 'axios'
import React, { useEffect, useState } from 'react'

export default function InputField() {
    const [data, setData] = useState([]); // Holds all books data
    const [search, setSearch] = useState(''); // The user's search query
    const [filteredOptions, setFilteredOptions] = useState([]); // Options to display based on the search query

    useEffect(() => {
        axios.get('https://softwium.com/api/books')
            .then(res => {
                setData(res.data);
            })
            .catch(error => {
                console.error("Error fetching data", error);
            });
    }, []);

    const handleSearchChange = (e) => {
        const query = e.target.value.toLowerCase();
        setSearch(query);

        if (query.length > 0) {
            const filtered = data.filter(book =>
                book.title.toLowerCase().includes(query) || 
                book.authors.some(author => author.toLowerCase().includes(query))
            );
            setFilteredOptions(filtered);
        } else {
            setFilteredOptions([]);
        }
    };

    return (
        <div>
            <input
                placeholder='Search for a book'
                onChange={handleSearchChange}
                value={search}
            />
            {filteredOptions.length > 0 && (
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {filteredOptions.map((book, index) => (
                        <li 
                            key={index} 
                            style={{ padding: '5px', cursor: 'pointer', border: '1px solid #ccc', margin: '5px 0' }}
                            onClick={() => console.log(`Selected book: ${book.title}, ISBN: ${book.isbn}`)}
                        >
                            {book.title} - {book.authors.join(', ')}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    )
}
